-- Cameras table schema for Supabase
-- Run this in Supabase SQL Editor

-- Drop existing table if exists
DROP TABLE IF EXISTS cameras;

-- Create cameras table
CREATE TABLE cameras (
  id SERIAL PRIMARY KEY,
  camera_index INTEGER UNIQUE NOT NULL,
  name TEXT NOT NULL,
  description TEXT,
  lat DOUBLE PRECISION NOT NULL,
  lng DOUBLE PRECISION NOT NULL,
  status TEXT NOT NULL DEFAULT 'offline' CHECK (status IN ('online', 'offline')),
  fov_angle INTEGER NOT NULL DEFAULT 60,
  fov_direction INTEGER NOT NULL DEFAULT 0,
  fov_distance INTEGER NOT NULL DEFAULT 50,
  rtsp_url TEXT,
  hls_url TEXT,
  created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),
  updated_at TIMESTAMP WITH TIME ZONE DEFAULT NOW()
);

-- Create index for faster status queries
CREATE INDEX idx_cameras_status ON cameras(status);

-- Enable RLS (Row Level Security)
ALTER TABLE cameras ENABLE ROW LEVEL SECURITY;

-- Allow public read access
CREATE POLICY "Allow public read" ON cameras FOR SELECT USING (true);
CREATE POLICY "Allow public update" ON cameras FOR UPDATE USING (true);

